{
  "Name": "GetFolderName",
  "Implementation": @"
    public static class GetFolderName
    {
        public static void Process(string path, out string imageName)
        {
            if (string.IsNullOrEmpty(path))
            {
                throw new Exception(\"The path provided is empty.\");
            }

            var directoryName = System.IO.Path.GetDirectoryName(path);
            var folderName = new string(directoryName.Where(c => char.IsLetterOrDigit(c)).ToArray()).ToLower();

            imageName = folderName;
        }
    }",
  "Using": ["System", "System.Linq"],
  "Assemblies": ["System.Core"]
}

