using System;
public static class RemoveTextBeforeStartTag {
	 public static void Process(string startTag, out string html)
{
    int index = html.IndexOf(startTag);
    if (index < 0)
    {
        throw new Exception("Start tag not found in the provided text.");
    }
    html = html.Substring(index);
} 
}
